import React,{Component} from 'react'
import schema from '../Schema/Employee_form'
import ui_schema from '../Schema/employee_ui_schema'
import Form from "react-jsonschema-form";
import empl_deta from '../Schema/Empl_Deta';
import {Button} from "reactstrap";


export default class Employee_deta extends Component{
    constructor(props){
        super(props)
        this.state ={
            id:'0',name: '',username:'',email:''}
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }
    handleChange(ev){
        this.setState({[ev.target.name]:ev.target.value})
    }
    handleSubmit(ev){
        ev.preventDefault()
    }
    render() {
        return(
                        <div className="bg-info">
                            <Form onSubmit={this.handleSubmit} onChange={this.handleChange} liveValidate= {true} className= "form-group text-gray-dark bg-faded font-weight-bold"  schema={schema} uiSchema={ui_schema} formData={empl_deta}>
                                <Button color="primary" className= "btn-outline-success m-2">Submit</Button>
                                <Button color="primary" className= "btn-outline-success m-2">Preview</Button>
                            </Form>
                        </div>
        )
    }
}
