import React,{Component} from 'react'
import schema from '../Schema/Employee_form'
import ui_schema from '../Schema/employee_ui_schema'
import Form from "react-jsonschema-form";
import empl_deta from '../Schema/Empl_Deta';
import {Button,Row,Col,Modal,ModalBody,Table} from "reactstrap";
import axios from 'axios'

var jsonData;


export default class Employee_deta extends Component{
    constructor(props) {
        super(props);
        this.state= {
            formData : [],
            modal : false
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.preview = this.preview.bind(this)
        this.toggle = this.toggle.bind(this)
    }
    handleSubmit(formData){
        axios.post(
                'http://localhost:3000/posts',this.state.formData)
                .then(res => {alert('Organisation Created')})
                .catch(res =>{alert('BAD REQUEST')})
        this.toggle()

    }
    toggle(){
        this.setState({modal:!this.state.modal})
    }
    preview(formData){
        this.setState({formData:formData.formData})
        this.toggle()
    }
    render() {
        return(
                        <div className="bg-info">
                            <Form onSubmit={this.preview} liveValidate= {false} className= "container-fluid bg-inverse text-white form-group"  schema={schema} uiSchema={ui_schema}>
                                <Button color="primary" className= "btn-outline-success m-3">Submit</Button>
                            </Form>
                            <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
                                <ModalBody>
                                    <Table>
                                        <thead>
                                                <th>Submited Fields</th>
                                                <th>Submited Entries</th>
                                        </thead>
                                        <tbody>
                                        </tbody>
                                    </Table>
                                    <Button className="btn-outline-danger" onClick={this.handleSubmit}>Submit</Button>
                                </ModalBody>
                            </Modal>
                        </div>
        )
    }
}
