import React,{Component} from 'react'
import schema from '../Schema/Employee_form'
import ui_schema from '../Schema/employee_ui_schema'
import Form from "react-jsonschema-form";
import empl_deta from '../Schema/Empl_Deta';
import {Button,Row,Col,Modal,ModalBody} from "reactstrap";
import axios from 'axios'


export default class Employee_deta extends Component{
    constructor(props) {
        super(props);
        this.state= {
            formData : [],
            modal : false
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.preview = this.preview.bind(this)
        this.toggle = this.toggle.bind(this)
    }
    handleSubmit(formData){
        this.setState({formData:formData})
      /*      axios.post(
                'http://localhost:3000/posts', formData.formData)
                .then(res => {alert('Organisation Created')})
                .catch(res =>{alert('BAD REQUEST')}) */

    }
    toggle(){
        this.setState({modal:!this.state.modal})
    }
    preview(formData){
        console.log(formData)
    }
    render() {
        return(
                        <div className="bg-info">
                            <Form onSubmit={this.handleSubmit} liveValidate= {false} className= "container-fluid bg-inverse text-white form-group"  schema={schema} uiSchema={ui_schema}>
                                <Button color="primary" className= "btn-outline-success m-3">Submit</Button>
                                <Button color="success" className= "btn-outline-danger m-3" onClick={this.preview}>Preview</Button>
                            </Form>
                            <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
                                <ModalBody>
                                    <p>Creating New Client</p>
                                    <Button className="btn-outline-danger">Submit</Button>
                                </ModalBody>
                            </Modal>
                        </div>
        )
    }
}
