                               },
                               "EmployeeDOC": {
                                 "type": "string",
                                 "title": "Date of Confirmation",
                                 "format": "date"
                               },
                               "EmployeeDOJ": {
                                 "type": "string",
                                 "title": "Date of Joining",
                                 "format": "date"
                               },
                               "EmployeeBloodGroup": {
                                 "type": "string",
                                 "title": "Blood Group",
                                 "enum": [
                                   "A+",
                                   "A-",
                                   "B+",
                                   "B-",
                                   "AB+",
                                   "AB-",
                                   "O+",
                                   "O-"
                                 ]
                               }
                             },
                             "additionalProperties": false,
                             "additionalItems" : false,
                             "required" : ["id"]
                           },
                           "EmployeeBankDetails": {
                             "type": "object",
                             "properties": {
                               "AccountNumber": {
                                 "type": "string",
                                 "title": "Employee Account Number"
                               },
                               "BankCode": {
                                 "type": "string",
                                 "title": "Bank Name"
                               },
                               "BranchCode": {
