                                 "enum": [
                                   "Active",
                                   "Resign",
                                   "Hold"
                                 ]
                               },
                               "EmployeeEmailID": {
                                 "type": "string",
                                 "title": "Email Address",
                                 "format": "email"
                               },
                               "EmployeeDepartment": {
                                 "type": "string",
                                 "title": "Department"
                               },
                               "EmployeeDesigantion": {
                                 "type": "string",
                                 "title": "Designation"
                               },
                               "EmployeeGrade": {
                                 "type": "string",
                                 "title": "Grade"
                               },
                               "EmployeeLocation": {
                                 "type": "string",
                                 "title": "Location"
                               },
                               "EmploymentType": {
                                 "type": "string",
                                 "title": "Employment Type",
                                 "enum": [
                                   "Permanent",
                                   "Interm",
                                   "Probationary"
                                 ]
                               },
                               "Currency": {
                                 "type": "string",
                                 "title": "Currency",
                                 "enum": [
                                   "Rupee",
                                   "Yan",
                                   "Dollar"
                                 ]
                               },
                               "SupervisiorNumber": {
                                 "type": "number",
                                 "title": "Supervisior Number"
                               }
                             }
                           }
                         }
                       }
)

export default class EmployeePersonalDetails extends Component{
    render() {
        return(
            <div>
                <Form schema= {schema} uiSchema={UiSchema}/>
            </div>
        )
    }
}
