import React,{Component} from "react";
import axios from 'axios';
import {Table,Button} from "reactstrap";


export default class EMPL_LIST extends Component{
    constructor(props) {
        super(props);
        this.state={jsondata:[]}
        this.componentDidMount = this.componentDidMount.bind(this)
        this.onClick = this.onClick.bind(this)
    }
    onClick(ev){
        axios.get('http://localhost:3000/posts?q=122'
        )
            .then(response=> {
                this.setState({jsondata:response.data})
                console.log(response);
            })
            .catch(function (error) {
                alert("BAD REQUEST")
            });
        ev.preventDefault();
    }
    componentDidMount() {
        axios.get('http://localhost:3000/posts'
        )
            .then(response=> {
               this.setState({jsondata:response.data})
                console.log(response);
            })
            .catch(function (error) {
                alert("BAD REQUEST")
            });
    }
    render(){
        return(
            <div className="container bg-faded m-lg-3">
                <input name = 'id' placeholder= 'Enter ID here to search' type = 'text'></input>
                <Button className='btn-outline-danger m-3' onClick={this.onClick} > Search </Button>
                <hr />
              <Table className='tab-content' hover>
                  <thead>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Author</th>
                  </thead>
                  <tbody>
                        {this.state.jsondata.map(emplid=>(
                            <tr >
                                <td key = {emplid}>{emplid.id}</td>
                                <td key = {emplid}>{emplid.title}</td>
                                <td key = {emplid}>{emplid.author}</td>
                            </tr>
                        ))}
                  </tbody>
              </Table>
            </div>
        )
    }
}
